import React, { useState, useEffect } from "react";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";

const FeelingProud = ({ theme }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Assuming you have an array of image URLs
  const images = [
    require("../../assests/images/home1.jpg"),
    require("../../assests/images/home2.jpg"),
    require("../../assests/images/home3.jpg"),
    // Add more image URLs as needed
  ];

  const totalSlides = images.length;

  useEffect(() => {
    const interval = setInterval(() => {
      const nextSlide = (currentSlide + 1) % totalSlides;
      setCurrentSlide(nextSlide);
    }, 3000); // Change the time (in milliseconds) to control the speed of autoscrolling

    return () => clearInterval(interval);
  }, [currentSlide, totalSlides]);

  return (
    <Carousel
      selectedItem={currentSlide}
      showArrows={false}
      showStatus={false}
      showIndicators={false}
      showThumbs={false}
      emulateTouch={true}
    >
      {images.map((image, index) => (
        <div key={index}>
          <img src={image} alt={`slide ${index}`} />
        </div>
      ))}
    </Carousel>
  );
};

export default FeelingProud;
