import React, { useState, useEffect } from "react";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";

const FeelingProud = ({ theme }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const totalSlides = 3; // Assuming there are 3 images in this example

  const images = [
    require("../../assests/images/home1.jpg"),
    require("../../assests/images/home2.jpg"),
    require("../../assests/images/home3.jpg"),
    // Add more images as needed
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      const nextSlide = (currentSlide + 1) % totalSlides;
      setCurrentSlide(nextSlide);
    }, 3000); // Change the time (in milliseconds) to control the speed of autoscrolling

    return () => clearInterval(interval);
  }, [currentSlide, totalSlides]);

  return (
    <div style={{ width: "100%", height: "400px", overflow: "hidden" }}>
      <Carousel
        selectedItem={currentSlide}
        showArrows={false}
        showStatus={false}
        showIndicators={false}
        showThumbs={false}
        emulateTouch={true}
      >
        {images.map((image, index) => (
          <div key={index}>
            <img src={image.src} alt={image.alt} style={{ height: "100%", objectFit: "contain" }} />
          </div>
        ))}
      </Carousel>
    </div>
  );
};

export default FeelingProud;
